mapper.update:
update P_EMPLOYEE
set E_SALARY = #{e_salary},
    e_state  = #{e_state},
    e_debit  = #{e_debit},
    e_phone  = #{e_phone}
where e_id = #{e_id}

update P_EMPLOYEE
set E_POS_ID = #{e_pos_id}
where e_id = #{e_id}

mapper.select:
select *
from P_EMPLOYEE

    1=1

        and e_type = #{e_type}


        and e_password = #{e_password}


        and e_account = #{e_account}


        and e_pos_id = #{e_pos_id}


        and e_id = #{e_id}


        and e_name = #{e_name}

select count(*) from P_EMPLOYEE
1 = 1
and e_pos_id = #{pos_id}
